import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// You can replace these with actual icons from libraries like heroicons or react-icons
const HomeIcon = () => <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"></path></svg>;
const GameIcon = () => <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z"></path><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>;
const TaskIcon = () => <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01"></path></svg>;
const UsersIcon = () => <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 018 0z"></path></svg>;

const Sidebar = ({ user }) => {
  const location = useLocation();
  const role = user?.role || 'participant';
  
  // Define menu items based on user role
  const getMenuItems = () => {
    const menuItems = [];
    
    // Common menu items
    menuItems.push(
      { name: 'Dashboard', icon: <HomeIcon />, path: '/' }
    );
    
    // Participant menu items
    if (['participant', 'instructor', 'admin'].includes(role)) {
      menuItems.push(
        { name: 'Join Game', icon: <GameIcon />, path: '/join-game' }
      );
    }
    
    // Instructor menu items
    if (['instructor', 'admin'].includes(role)) {
      menuItems.push(
        { name: 'Instructor Dashboard', icon: <HomeIcon />, path: '/instructor/dashboard' },
        { name: 'Create Game', icon: <GameIcon />, path: '/create-game' }
      );
    }
    
    // Admin menu items
    if (role === 'admin') {
      menuItems.push(
        { name: 'Admin Dashboard', icon: <HomeIcon />, path: '/admin/dashboard' },
        { name: 'User Management', icon: <UsersIcon />, path: '/admin/users' }
      );
    }
    
    return menuItems;
  };
  
  const menuItems = getMenuItems();
  
  return (
    <div className="w-64 bg-white shadow-lg hidden md:block">
      <div className="h-16 flex items-center justify-center bg-blue-600">
        <h1 className="text-xl font-bold text-white">Leadership App</h1>
      </div>
      
      <div className="px-4 py-6">
        <div className="flex items-center space-x-3 mb-6">
          <div className="h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
            <span className="text-blue-600 font-medium">{user?.name?.charAt(0) || 'U'}</span>
          </div>
          <div>
            <p className="text-sm font-medium">{user?.name || 'User'}</p>
            <p className="text-xs text-gray-500 capitalize">{role}</p>
          </div>
        </div>
        
        <nav className="space-y-1">
          {menuItems.map((item, index) => (
            <Link
              key={index}
              to={item.path}
              className={`flex items-center px-4 py-3 text-sm rounded-lg transition-colors ${
                location.pathname === item.path
                  ? 'bg-blue-50 text-blue-600 font-medium'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <span className="mr-3">{item.icon}</span>
              {item.name}
            </Link>
          ))}
        </nav>
      </div>
    </div>
  );
};

export default Sidebar;
