import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../../utils/api';
import { toast } from 'react-toastify';

const GameCreate = () => {
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // Game form state
  const [gameForm, setGameForm] = useState({
    title: '',
    description: '',
    accessCode: '',
    isActive: true,
    endTime: ''
  });
  
  // Handle form changes
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setGameForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };
  
  // Generate random access code
  const generateAccessCode = () => {
    const characters = 'ABCDEFGHJKLMNPQRSTUVWXYZ23456789'; // Omitting similar looking characters
    let result = '';
    for (let i = 0; i < 6; i++) {
      result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    
    setGameForm(prev => ({
      ...prev,
      accessCode: result
    }));
  };
  
  // Handle game creation
  const handleCreateGame = async (e) => {
    e.preventDefault();
    
    try {
      setIsSubmitting(true);
      
      // Format game data
      const gameData = {
        ...gameForm,
        // Convert empty string to null for endTime
        endTime: gameForm.endTime || null,
        // Set start time to now
        startTime: new Date()
      };
      
      // Create game
      await api.post('/api/games', gameData);
      
      toast.success('Spil oprettet!');
      
      // Redirect to dashboard after successful creation
      navigate('/instructor/dashboard');
      
    } catch (error) {
      console.error('Error creating game:', error);
      toast.error(error.response?.data?.message || 'Der opstod en fejl ved oprettelse af spillet');
      setIsSubmitting(false);
    }
  };
  
  return (
    <div className="max-w-2xl mx-auto py-8 px-4">
      <h1 className="text-3xl font-bold text-blue-900 mb-6">Opret nyt spil</h1>
      
      <form onSubmit={handleCreateGame} className="bg-white rounded-lg shadow-md p-6">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
            Spiltitel *
          </label>
          <input
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="title"
            type="text"
            name="title"
            value={gameForm.title}
            onChange={handleFormChange}
            placeholder="F.eks. Biblioteksorienteringsløb"
            required
          />
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
            Beskrivelse
          </label>
          <textarea
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="description"
            name="description"
            rows="4"
            value={gameForm.description}
            onChange={handleFormChange}
            placeholder="Beskriv formål og indhold af spillet"
          ></textarea>
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="accessCode">
            Adgangskode *
          </label>
          <div className="flex">
            <input
              className="appearance-none border rounded-l w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline font-mono"
              id="accessCode"
              type="text"
              name="accessCode"
              value={gameForm.accessCode}
              onChange={handleFormChange}
              placeholder="F.eks. XYZ123"
              required
            />
            <button
              type="button"
              onClick={generateAccessCode}
              className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded-r"
            >
              Generer
            </button>
          </div>
          <p className="text-xs text-gray-500 mt-1">
            Dette er koden, som deltagerne skal bruge for at tilslutte sig spillet.
          </p>
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="endTime">
            Slutdato (valgfri)
          </label>
          <input
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="endTime"
            type="datetime-local"
            name="endTime"
            value={gameForm.endTime}
            onChange={handleFormChange}
          />
          <p className="text-xs text-gray-500 mt-1">
            Lad være tom for at holde spillet aktivt uden tidsbegrænsning.
          </p>
        </div>
        
        <div className="mb-6">
          <div className="flex items-center">
            <input
              id="isActive"
              type="checkbox"
              name="isActive"
              checked={gameForm.isActive}
              onChange={handleFormChange}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
            <label htmlFor="isActive" className="ml-2 block text-sm text-gray-900">
              Aktiver spillet med det samme
            </label>
          </div>
          <p className="text-xs text-gray-500 mt-1">
            Hvis deaktiveret, kan du aktivere spillet senere.
          </p>
        </div>
        
        <div className="flex items-center justify-between">
          <button
            type="button"
            onClick={() => navigate('/instructor/dashboard')}
            className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
          >
            Annuller
          </button>
          
          <button
            type="submit"
            className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <span className="flex items-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Opretter...
              </span>
            ) : (
              'Opret spil'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default GameCreate;
