import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import api from '../../utils/api';
import { toast } from 'react-toastify';
import { useAuth } from '../../contexts/AuthContext';

const TaskList = () => {
  const { gameId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  
  const [game, setGame] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [team, setTeam] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  
  // Filter states
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  
  // Fetch game data, tasks, and user team
  useEffect(() => {
    const fetchGameData = async () => {
      try {
        setIsLoading(true);
        
        // Get game details
        const gameResponse = await api.games.getById(gameId);
        setGame(gameResponse.data);
        
        // Get tasks for this game
        const tasksResponse = await api.tasks.getByGame(gameId);
        setTasks(tasksResponse.data);
        
        // Get user's team for this game
        const userTeamsResponse = await api.get('/api/teams/user');
        const teamForGame = userTeamsResponse.data.find(t => t.game._id === gameId);
        
        if (teamForGame) {
          setTeam(teamForGame);
          
          // Get submissions for this team
          const submissionsResponse = await api.submissions.getByTeam(teamForGame._id);
          setSubmissions(submissionsResponse.data);
        }
      } catch (error) {
        console.error('Error fetching game data:', error);
        toast.error('Der opstod en fejl ved hentning af spildata');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchGameData();
  }, [gameId]);
  
  // If no team is found, redirect user to join team
  useEffect(() => {
    if (!isLoading && game && !team) {
      toast.info('Du skal først være medlem af et hold for at se opgaver');
      navigate(`/game/${gameId}/join-team`);
    }
  }, [isLoading, game, team, gameId, navigate]);
  
  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-gray-500">Indlæser opgaver...</p>
      </div>
    );
  }
  
  if (!game) {
    return (
      <div className="text-center py-8">
        <h2 className="text-2xl font-bold text-red-600 mb-4">Spil ikke fundet</h2>
        <p className="text-gray-600 mb-6">Det valgte spil findes ikke eller du har ikke adgang til det.</p>
        <Link to="/join-game" className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded">
          Tilbage til Find spil
        </Link>
      </div>
    );
  }
  
  // Get unique categories for filtering
  const categories = ['all', ...new Set(tasks.map(task => task.category))];
  
  // Check if a task is submitted
  const isTaskSubmitted = (taskId) => {
    return submissions.some(s => s.task._id === taskId);
  };
  
  // Get submission status for a task
  const getSubmissionStatus = (taskId) => {
    const submission = submissions.find(s => s.task._id === taskId);
    if (!submission) return null;
    
    return {
      isEvaluated: submission.isEvaluated,
      pointsEarned: submission.pointsEarned
    };
  };
  
  // Filter tasks based on category, status, and search
  const filteredTasks = tasks.filter(task => {
    const matchesCategory = categoryFilter === 'all' || task.category === categoryFilter;
    
    let matchesStatus = true;
    if (statusFilter === 'submitted') {
      matchesStatus = isTaskSubmitted(task._id);
    } else if (statusFilter === 'not_submitted') {
      matchesStatus = !isTaskSubmitted(task._id);
    } else if (statusFilter === 'evaluated') {
      const status = getSubmissionStatus(task._id);
      matchesStatus = status && status.isEvaluated;
    }
    
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                          task.description.toLowerCase().includes(searchQuery.toLowerCase());
    
    return matchesCategory && matchesStatus && matchesSearch;
  });

  return (
    <div className="max-w-6xl mx-auto">
      <div className="flex flex-col md:flex-row justify-between items-start mb-6">
        <div>
          <h1 className="text-3xl font-bold text-blue-900 mb-1">{game.title}</h1>
          <p className="text-gray-600">
            <Link to={`/team-dashboard/${team._id}`} className="text-blue-600 hover:text-blue-800">
              {team.name}
            </Link> - Opgaveoversigt
          </p>
        </div>
        
        <div className="mt-4 md:mt-0">
          <Link
            to={`/team-dashboard/${team._id}`}
            className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded inline-flex items-center"
          >
            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
            </svg>
            Tilbage til holdsiden
          </Link>
        </div>
      </div>
      
      {/* Filters */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="flex flex-col md:flex-row md:items-center justify-between space-y-4 md:space-y-0">
          <div className="md:w-1/3">
            <input
              type="text"
              placeholder="Søg efter opgaver..."
              className="w-full px-4 py-2 border rounded-md"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
          
          <div className="flex space-x-4">
            <div>
              <label htmlFor="categoryFilter" className="block text-sm font-medium text-gray-700 mb-1">
                Kategori
              </label>
              <select
                id="categoryFilter"
                className="px-4 py-2 border rounded-md"
                value={categoryFilter}
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <option value="all">Alle kategorier</option>
                {categories
                  .filter(category => category !== 'all')
                  .map(category => (
                    <option key={category} value={category}>
                      {category}
                    </option>
                  ))
                }
              </select>
            </div>
            
            <div>
              <label htmlFor="statusFilter" className="block text-sm font-medium text-gray-700 mb-1">
                Status
              </label>
              <select
                id="statusFilter"
                className="px-4 py-2 border rounded-md"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
              >
                <option value="all">Alle opgaver</option>
                <option value="not_submitted">Ikke løst</option>
                <option value="submitted">Indsendt</option>
                <option value="evaluated">Bedømt</option>
              </select>
            </div>
          </div>
        </div>
      </div>
      
      {/* Task list */}
      {filteredTasks.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredTasks.map(task => {
            const submissionStatus = getSubmissionStatus(task._id);
            
            return (
              <div key={task._id} className="bg-white rounded-lg shadow-md overflow-hidden">
                <div className="p-6">
                  <div className="flex justify-between items-start mb-2">
                    <h2 className="text-xl font-semibold text-gray-800">{task.title}</h2>
                    <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">
                      {task.category}
                    </span>
                  </div>
                  
                  <p className="text-gray-600 mb-4 line-clamp-3">{task.description}</p>
                  
                  <div className="flex justify-between text-sm mb-4">
                    <div>
                      <span className="text-red-600 font-medium">Risiko: {task.riskPoints}</span>
                    </div>
                    <div>
                      <span className="text-green-600 font-medium">Belønning: {task.rewardPoints}</span>
                    </div>
                  </div>
                  
                  <div className="flex items-center text-xs text-gray-500 mb-4">
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                    </svg>
                    <span>{task.timeLimit} minutter</span>
                    
                    <span className="mx-2">•</span>
                    
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
                    </svg>
                    <span>
                      {task.type === 'multiple_choice' ? 'Multiple choice' : ''}
                      {task.type === 'text' ? 'Tekstbesvarelse' : ''}
                      {task.type === 'video' ? 'Videobesvarelse' : ''}
                    </span>
                  </div>
                  
                  {submissionStatus ? (
                    <div className="mt-4 border-t pt-4">
                      {submissionStatus.isEvaluated ? (
                        <div className="flex justify-between items-center">
                          <span className="text-sm font-medium text-gray-900">Bedømt</span>
                          <span className={`text-sm font-medium px-2 py-1 rounded ${
                            submissionStatus.pointsEarned >= 0 ? 
                            'bg-green-100 text-green-800' : 
                            'bg-red-100 text-red-800'
                          }`}>
                            {submissionStatus.pointsEarned >= 0 ? '+' : ''}{submissionStatus.pointsEarned} point
                          </span>
                        </div>
                      ) : (
                        <div className="flex justify-center">
                          <span className="text-sm bg-yellow-100 text-yellow-800 px-2 py-1 rounded">
                            Afventer bedømmelse
                          </span>
                        </div>
                      )}
                    </div>
                  ) : (
                    <Link
                      to={`/solve-task/${task._id}`}
                      className="mt-4 bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded block text-center"
                    >
                      Løs opgave
                    </Link>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="bg-white p-8 rounded-lg shadow-md text-center">
          <h2 className="text-xl font-bold text-gray-800 mb-4">Ingen opgaver fundet</h2>
          <p className="text-gray-600">
            Der er ingen opgaver, der matcher dine filtreringskriterier.
          </p>
          {statusFilter !== 'all' || categoryFilter !== 'all' || searchQuery ? (
            <button
              onClick={() => {
                setCategoryFilter('all');
                setStatusFilter('all');
                setSearchQuery('');
              }}
              className="mt-4 text-blue-600 hover:text-blue-800"
            >
              Nulstil filtre
            </button>
          ) : (
            <p className="mt-4 text-gray-500">
              Der er endnu ikke oprettet nogen opgaver til dette spil.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default TaskList;
