import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../../utils/api';
import { toast } from 'react-toastify';
import { useAuth } from '../../contexts/AuthContext';

const TaskSolve = () => {
  const { taskId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  
  const [task, setTask] = useState(null);
  const [game, setGame] = useState(null);
  const [team, setTeam] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [timeLeft, setTimeLeft] = useState(null);
  const [startTime, setStartTime] = useState(null);
  
  // Form state
  const [answer, setAnswer] = useState('');
  const [selectedOption, setSelectedOption] = useState('');
  const [videoFile, setVideoFile] = useState(null);
  const videoInputRef = useRef(null);
  
  // Timer ref for cleanup
  const timerRef = useRef(null);
  
  // Fetch task data and check if user has access
  useEffect(() => {
    const fetchTaskData = async () => {
      try {
        setIsLoading(true);
        
        // Get task details
        const taskResponse = await api.tasks.getById(taskId);
        setTask(taskResponse.data);
        
        // Get game details
        const gameResponse = await api.games.getById(taskResponse.data.game);
        setGame(gameResponse.data);
        
        // Get user's team for this game
        const userTeamsResponse = await api.get('/api/teams/user');
        const teamForGame = userTeamsResponse.data.find(t => t.game._id === taskResponse.data.game);
        
        if (!teamForGame) {
          toast.error('Du har ikke adgang til denne opgave');
          navigate('/join-game');
          return;
        }
        
        setTeam(teamForGame);
        
        // Check if task is already submitted
        const submissionsResponse = await api.submissions.getByTeam(teamForGame._id);
        const isAlreadySubmitted = submissionsResponse.data.some(s => s.task._id === taskId);
        
        if (isAlreadySubmitted) {
          toast.info('Du har allerede indsendt denne opgave');
          navigate(`/team-dashboard/${teamForGame._id}`);
          return;
        }
        
        // Start the timer
        setStartTime(Date.now());
        setTimeLeft(taskResponse.data.timeLimit * 60); // Convert minutes to seconds
      } catch (error) {
        console.error('Error fetching task data:', error);
        toast.error('Der opstod en fejl ved hentning af opgavedata');
        navigate('/join-game');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchTaskData();
    
    // Cleanup function
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [taskId, navigate]);
  
  // Timer effect
  useEffect(() => {
    if (startTime && timeLeft !== null) {
      timerRef.current = setInterval(() => {
        const elapsedSeconds = Math.floor((Date.now() - startTime) / 1000);
        const remaining = task.timeLimit * 60 - elapsedSeconds;
        
        if (remaining <= 0) {
          clearInterval(timerRef.current);
          setTimeLeft(0);
          toast.error('Tiden er udløbet! Din besvarelse blev ikke gemt.');
          navigate(`/tasks/${task.game}`);
        } else {
          setTimeLeft(remaining);
        }
      }, 1000);
    }
    
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [startTime, task, navigate]);
  
  // Format time left
  const formatTimeLeft = () => {
    if (timeLeft === null) return '--:--';
    
    const minutes = Math.floor(timeLeft / 60);
    const seconds = timeLeft % 60;
    
    return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };
  
  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate answer
    if (task.type === 'multiple_choice' && !selectedOption) {
      toast.error('Vælg venligst en mulighed');
      return;
    }
    
    if (task.type === 'text' && !answer.trim()) {
      toast.error('Skriv venligst et svar');
      return;
    }
    
    if (task.type === 'video' && !videoFile) {
      toast.error('Upload venligst en video');
      return;
    }
    
    try {
      setSubmitting(true);
      
      const submissionData = {
        task: taskId,
        team: team._id,
        answer: task.type === 'multiple_choice' ? selectedOption : answer
      };
      
      let response;
      
      if (task.type === 'video') {
        response = await api.submissions.createWithFile(submissionData, videoFile);
      } else {
        response = await api.submissions.create(submissionData);
      }
      
      toast.success('Opgave indsendt til bedømmelse!');
      navigate(`/team-dashboard/${team._id}`);
    } catch (error) {
      console.error('Error submitting task:', error);
      toast.error(error.response?.data?.message || 'Der opstod en fejl ved indsendelse af opgaven');
    } finally {
      setSubmitting(false);
    }
  };
  
  // Handle video file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    
    if (!file) {
      setVideoFile(null);
      return;
    }
    
    // Validate file type
    if (!file.type.startsWith('video/')) {
      toast.error('Filen skal være en video');
      return;
    }
    
    // Validate file size (max 100MB)
    if (file.size > 100 * 1024 * 1024) {
      toast.error('Videoen må højst være 100MB');
      return;
    }
    
    setVideoFile(file);
  };
  
  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <p className="text-gray-500">Indlæser opgave...</p>
      </div>
    );
  }
  
  if (!task || !game || !team) {
    return (
      <div className="text-center py-8">
        <h2 className="text-2xl font-bold text-red-600 mb-4">Opgave ikke fundet</h2>
        <p className="text-gray-600 mb-6">Opgaven findes ikke eller du har ikke adgang til den.</p>
        <Link to="/join-game" className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded">
          Tilbage til Find spil
        </Link>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      {/* Header with timer */}
      <div className="bg-white p-4 rounded-lg shadow-md mb-6 flex justify-between items-center">
        <div>
          <Link 
            to={`/tasks/${task.game}`}
            className="text-blue-600 hover:text-blue-800 flex items-center"
          >
            <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
            </svg>
            Tilbage til opgaver
          </Link>
          <h1 className="text-2xl font-bold text-gray-800 mt-1">{task.title}</h1>
        </div>
        
        <div className="text-center">
          <div className="text-sm text-gray-500 mb-1">Tid tilbage</div>
          <div className={`font-mono text-xl font-bold ${
            timeLeft && timeLeft < 60 ? 'text-red-600 animate-pulse' : 
            timeLeft && timeLeft < 300 ? 'text-orange-600' : 
            'text-gray-800'
          }`}>
            {formatTimeLeft()}
          </div>
        </div>
      </div>
      
      {/* Task details */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="flex justify-between mb-4">
          <span className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
            {task.category}
          </span>
          
          <span className="text-sm text-gray-500">
            {task.type === 'multiple_choice' ? 'Multiple choice' : ''}
            {task.type === 'text' ? 'Tekstbesvarelse' : ''}
            {task.type === 'video' ? 'Videobesvarelse' : ''}
          </span>
        </div>
        
        <div className="mb-6">
          <p className="text-gray-800 whitespace-pre-line">{task.description}</p>
        </div>
        
        <div className="flex justify-between text-sm mb-2 pt-4 border-t">
          <div>
            <span className="text-red-600 font-medium">Risiko: -{task.riskPoints} point</span>
          </div>
          <div>
            <span className="text-green-600 font-medium">Belønning: +{task.rewardPoints} point</span>
          </div>
        </div>
      </div>
      
      {/* Answer form */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Din besvarelse</h2>
        
        <form onSubmit={handleSubmit}>
          {task.type === 'multiple_choice' && (
            <div className="space-y-3">
              {task.options.map((option, index) => (
                <div key={index} className="flex items-center">
                  <input
                    type="radio"
                    id={`option-${index}`}
                    name="answer"
                    value={option}
                    checked={selectedOption === option}
                    onChange={() => setSelectedOption(option)}
                    className="h-4 w-4 text-blue-600 focus:ring-blue-500"
                  />
                  <label htmlFor={`option-${index}`} className="ml-3 block text-gray-700">
                    {option}
                  </label>
                </div>
              ))}
            </div>
          )}
          
          {task.type === 'text' && (
            <textarea
              value={answer}
              onChange={(e) => setAnswer(e.target.value)}
              rows="6"
              className="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Skriv dit svar her..."
            ></textarea>
          )}
          
          {task.type === 'video' && (
            <div className="space-y-4">
              <div className="flex items-center justify-center w-full">
                <label className="flex flex-col items-center justify-center w-full h-32 border-2 border-gray-300 border-dashed rounded-lg cursor-pointer bg-gray-50 hover:bg-gray-100">
                  <div className="flex flex-col items-center justify-center pt-5 pb-6">
                    <svg className="w-10 h-10 mb-3 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"></path>
                    </svg>
                    <p className="mb-2 text-sm text-gray-500">
                      <span className="font-semibold">Klik for at uploade</span> eller træk og slip
                    </p>
                    <p className="text-xs text-gray-500">
                      Video (MP4, MOV, max 100MB)
                    </p>
                  </div>
                  <input 
                    type="file" 
                    className="hidden" 
                    accept="video/*"
                    ref={videoInputRef}
                    onChange={handleFileChange}
                  />
                </label>
              </div>
              
              {videoFile && (
                <div className="bg-blue-50 p-3 rounded-md">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-gray-800 truncate">
                      {videoFile.name}
                    </span>
                    <span className="text-xs text-gray-500">
                      {(videoFile.size / (1024 * 1024)).toFixed(2)} MB
                    </span>
                  </div>
                  <button
                    type="button"
                    className="text-xs text-red-600 hover:text-red-800 mt-1"
                    onClick={() => {
                      setVideoFile(null);
                      if (videoInputRef.current) {
                        videoInputRef.current.value = '';
                      }
                    }}
                  >
                    Fjern video
                  </button>
                </div>
              )}
            </div>
          )}
          
          <div className="flex justify-between mt-6">
            <Link
              to={`/tasks/${task.game}`}
              className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
            >
              Annuller
            </Link>
            
            <button
              type="submit"
              className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-6 rounded"
              disabled={submitting}
            >
              {submitting ? 'Indsender...' : 'Indsend besvarelse'}
            </button>
          </div>
        </form>
      </div>
      
      {/* Help text */}
      <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6">
        <div className="flex items-start">
          <div className="flex-shrink-0">
            <svg className="h-5 w-5 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
            </svg>
          </div>
          <div className="ml-3">
            <p className="text-sm text-yellow-700">
              Husk at du risikerer at miste {task.riskPoints} point, hvis din besvarelse vurderes til at være forkert.
              Omvendt kan du tjene {task.rewardPoints} point for en korrekt besvarelse.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskSolve;
