import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { api } from '../../utils/api';
import { toast } from 'react-toastify';

// Components
import StatCard from '../common/StatCard';
import TeamScoreTable from '../tables/TeamScoreTable';
import TaskCompletionChart from '../charts/TaskCompletionChart';
import LoadingSpinner from '../common/LoadingSpinner';

const GameStatistics = () => {
  const { gameId } = useParams();
  
  const [game, setGame] = useState(null);
  const [teams, setTeams] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    const fetchGameStatistics = async () => {
      try {
        setIsLoading(true);
        setError(null);
        
        // Get game details - FIXED: Use api.get instead of api.games.getById
        const gameResponse = await api.get(`/api/games/${gameId}`);
        setGame(gameResponse.data);
        
        // Get teams for this game
        const teamsResponse = await api.get(`/api/games/${gameId}/teams`);
        setTeams(teamsResponse.data);
        
        // Get tasks for this game
        const tasksResponse = await api.get(`/api/games/${gameId}/tasks`);
        setTasks(tasksResponse.data);
        
        // Get submissions for this game
        const submissionsResponse = await api.get(`/api/games/${gameId}/submissions`);
        setSubmissions(submissionsResponse.data);
        
      } catch (error) {
        console.error('Error fetching game statistics:', error);
        setError('Der opstod en fejl ved hentning af spilstatistik');
        toast.error('Der opstod en fejl ved hentning af spilstatistik');
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchGameStatistics();
  }, [gameId]);
  
  // Calculate overall statistics
  const calculateStats = () => {
    if (!game || !teams || !submissions) return null;
    
    const pendingSubmissions = submissions.filter(s => !s.isEvaluated);
    const evaluatedSubmissions = submissions.filter(s => s.isEvaluated);
    const correctSubmissions = evaluatedSubmissions.filter(s => s.isCorrect);
    
    return {
      teamCount: teams.length,
      taskCount: tasks.length,
      submissionCount: submissions.length,
      pendingCount: pendingSubmissions.length,
      correctCount: correctSubmissions.length,
      incorrectCount: evaluatedSubmissions.length - correctSubmissions.length,
      participantCount: teams.reduce((acc, team) => acc + (team.members?.length || 0), 0),
      completionRate: tasks.length > 0 ? (correctSubmissions.length / (teams.length * tasks.length) * 100).toFixed(1) : 0
    };
  };
  
  // Calculate task-specific statistics
  const calculateTaskStats = () => {
    if (!tasks || !submissions) return [];
    
    return tasks.map(task => {
      const taskSubmissions = submissions.filter(s => s.task?._id === task._id);
      const evaluatedSubmissions = taskSubmissions.filter(s => s.isEvaluated);
      const correctSubmissions = evaluatedSubmissions.filter(s => s.isCorrect);
      
      return {
        taskId: task._id,
        title: task.title,
        type: task.type,
        attempts: taskSubmissions.length,
        pending: taskSubmissions.filter(s => !s.isEvaluated).length,
        correct: correctSubmissions.length,
        incorrect: evaluatedSubmissions.length - correctSubmissions.length,
        completionRate: teams.length > 0 ? (correctSubmissions.length / teams.length * 100).toFixed(1) : 0
      };
    });
  };
  
  const stats = calculateStats();
  const taskStats = calculateTaskStats();
  
  if (isLoading) {
    return <LoadingSpinner message="IndlÃ¦ser statistik..." />;
  }
  
  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-md">
        <p className="text-red-600">{error}</p>
        <Link to="/admin/dashboard" className="text-blue-600 hover:underline">
          Tilbage til dashboard
        </Link>
      </div>
    );
  }
  
  if (!game) {
    return (
      <div className="text-center py-8">
        <h2 className="text-2xl font-bold text-red-600 mb-4">Spil ikke fundet</h2>
        <p className="text-gray-600 mb-6">Det valgte spil findes ikke eller du har ikke adgang til det.</p>
        <Link to="/admin/dashboard" className="bg-blue-800 hover:bg-blue-900 text-white font-bold py-2 px-4 rounded">
          Tilbage til dashboard
        </Link>
      </div>
    );
  }
  
  return (
    <div className="max-w-6xl mx-auto px-4 py-6">
      <div className="flex flex-col md:flex-row justify-between items-start mb-6">
        <div>
          <h1 className="text-3xl font-bold text-blue-900 mb-2">{game.title} - Statistik</h1>
          <p className="text-gray-600">
            Oprettet {new Date(game.createdAt).toLocaleDateString('da-DK')}
            {game.endTime && ` Â· Slutter ${new Date(game.endTime).toLocaleDateString('da-DK')}`}
          </p>
        </div>
        
        <div className="mt-4 md:mt-0">
          <Link 
            to={`/admin/games/${gameId}`}
            className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-2 px-4 rounded inline-flex items-center"
          >
            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
            </svg>
            Tilbage til spil
          </Link>
        </div>
      </div>
      
      {/* Overall Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <StatCard 
          title="Hold" 
          value={stats?.teamCount || 0} 
          description={`${stats?.participantCount || 0} deltagere i alt`}
          icon={
            <svg className="w-8 h-8 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z"></path>
            </svg>
          }
        />
        
        <StatCard 
          title="Opgaver" 
          value={stats?.taskCount || 0}
          icon={
            <svg className="w-8 h-8 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4"></path>
            </svg>
          }
        />
        
        <StatCard 
          title="Besvarelser" 
          value={stats?.submissionCount || 0}
          description={`${stats?.pendingCount || 0} afventer bedÃ¸mmelse`}
          icon={
            <svg className="w-8 h-8 text-purple-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"></path>
            </svg>
          }
        />
        
        <StatCard 
          title="GennemfÃ¸relse" 
          value={`${stats?.completionRate || 0}%`}
          description={`${stats?.correctCount || 0} korrekte svar`}
          icon={
            <svg className="w-8 h-8 text-amber-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"></path>
            </svg>
          }
        />
      </div>
      
      {/* Team Score Table */}
      <div className="bg-white rounded-lg shadow-md mb-8">
        <div className="px-6 py-4 border-b">
          <h2 className="text-xl font-semibold text-gray-800">Holdstillinger</h2>
        </div>
        <div className="p-6">
          <TeamScoreTable teams={teams} tasks={tasks} submissions={submissions} />
        </div>
      </div>
      
      {/* Task Completion Chart */}
      <div className="bg-white rounded-lg shadow-md mb-8">
        <div className="px-6 py-4 border-b">
          <h2 className="text-xl font-semibold text-gray-800">Opgavestatistik</h2>
        </div>
        <div className="p-6">
          <TaskCompletionChart taskStats={taskStats} />
        </div>
      </div>
      
      {/* Task Details */}
      <div className="bg-white rounded-lg shadow-md">
        <div className="px-6 py-4 border-b">
          <h2 className="text-xl font-semibold text-gray-800">Opgavedetaljer</h2>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Opgavetitel
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Besvarelser
                </th>
                <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Korrekte
                </th>
                <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Forkerte
                </th>
                <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Afventer
                </th>
                <th scope="col" className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                  GennemfÃ¸relse
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {taskStats.map(task => (
                <tr key={task.taskId} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{task.title}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                      ${task.type === 'multiple_choice' ? 'bg-green-100 text-green-800' : ''}
                      ${task.type === 'text' ? 'bg-blue-100 text-blue-800' : ''}
                      ${task.type === 'video' ? 'bg-purple-100 text-purple-800' : ''}
                    `}>
                      {task.type === 'multiple_choice' ? 'Multiple choice' : ''}
                      {task.type === 'text' ? 'Tekst' : ''}
                      {task.type === 'video' ? 'Video' : ''}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-center">
                    <div className="text-sm text-gray-900">{task.attempts}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-center">
                    <div className="text-sm text-green-600 font-medium">{task.correct}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-center">
                    <div className="text-sm text-red-600 font-medium">{task.incorrect}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-center">
                    <div className="text-sm text-amber-600 font-medium">{task.pending}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-center">
                    <div className="flex items-center justify-center">
                      <div className="w-24 bg-gray-200 rounded-full h-2.5">
                        <div 
                          className="bg-blue-600 h-2.5 rounded-full" 
                          style={{ width: `${task.completionRate}%` }}
                        ></div>
                      </div>
                      <span className="ml-2 text-sm text-gray-900">{task.completionRate}%</span>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default GameStatistics;
