const TestComponent = () => {
  return (
    <div className="fixed top-0 left-0 right-0 z-50 bg-red-500 text-white p-4 text-center font-bold">
      This is a Tailwind CSS test
    </div>
  );
};

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Layout components
import Navbar from './components/common/Navbar';
import Footer from './components/common/Footer';
import Sidebar from './components/common/Sidebar'; // New component

// Public pages
import Login from './pages/Login';
import Register from './pages/Register';
import Home from './pages/Home';

// Participant pages
import GameJoin from './pages/participant/GameJoin';
import TaskList from './pages/participant/TaskList';
import TaskSolve from './pages/participant/TaskSolve';
import TeamDashboard from './pages/participant/TeamDashboard';
import Reflection from './pages/participant/Reflection';

// Instructor pages
import GameCreate from './pages/instructor/GameCreate';
import GameManage from './pages/instructor/GameManage';
import TaskCreate from './pages/instructor/TaskCreate';
import SubmissionEvaluate from './pages/instructor/SubmissionEvaluate';
import InstructorDashboard from './pages/instructor/InstructorDashboard';

// Admin pages
import AdminDashboard from './pages/admin/AdminDashboard';
import UserManagement from './pages/admin/UserManagement';
import GameStatistics from './pages/admin/GameStatistics';

// Protected route component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const { isAuthenticated, user, loading } = useAuth();
  
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-gray-50">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-600"></div>
      </div>
    );
  }
  
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }
  
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    return <Navigate to="/" />;
  }
  
  return children;
};

// Dashboard layout for authenticated pages
const DashboardLayout = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  
  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar for desktop */}
      <Sidebar />
      
      {/* Mobile sidebar with overlay */}
      {sidebarOpen && (
        <div className="fixed inset-0 z-40 lg:hidden" onClick={() => setSidebarOpen(false)}>
          <div className="absolute inset-0 bg-gray-600 opacity-75"></div>
          <div className="absolute inset-y-0 left-0 flex flex-col w-64 max-w-xs bg-white">
            <div className="h-16 flex items-center px-6 border-b border-gray-200">
              <h1 className="text-xl font-bold text-blue-800">Leadership App</h1>
              <button 
                className="ml-auto text-gray-500 hover:text-gray-700"
                onClick={() => setSidebarOpen(false)}
              >
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <Sidebar />
          </div>
        </div>
      )}
      
      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Top navigation */}
        <div className="bg-white shadow-sm z-10">
          <div className="flex h-16 items-center justify-between px-4 sm:px-6">
            {/* Mobile menu button */}
            <button
              onClick={() => setSidebarOpen(true)}
              className="lg:hidden text-gray-500 hover:text-gray-700 focus:outline-none"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
            
            {/* Page title (can be dynamic based on route) */}
            <div className="flex-1 lg:ml-6">
              <h2 className="text-lg font-medium text-gray-800">Dashboard</h2>
            </div>
            
            {/* User menu (optional) */}
            <div className="flex items-center space-x-4">
              <button className="bg-gray-100 p-1 rounded-full text-gray-600 hover:text-gray-800 focus:outline-none">
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                </svg>
              </button>
            </div>
          </div>
        </div>
        
        {/* Main content */}
        <main className="flex-1 overflow-y-auto bg-gray-50 p-4 sm:p-6 lg:p-8">
          {children}
        </main>
      </div>
    </div>
  );
};

// Public layout component for unauthenticated pages
const PublicLayout = ({ children }) => {
  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      <Navbar />
      <main className="flex-grow">
        {children}
      </main>
      <Footer />
    </div>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Public routes with PublicLayout */}
          <Route path="/" element={<PublicLayout><Home /></PublicLayout>} />
          <Route path="/login" element={<PublicLayout><Login /></PublicLayout>} />
          <Route path="/register" element={<PublicLayout><Register /></PublicLayout>} />
          
          {/* Participant routes */}
          <Route 
            path="/join-game" 
            element={
              <ProtectedRoute allowedRoles={['participant', 'instructor', 'admin']}>
                <DashboardLayout>
                  <GameJoin />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/team-dashboard/:teamId" 
            element={
              <ProtectedRoute allowedRoles={['participant', 'instructor', 'admin']}>
                <DashboardLayout>
                  <TeamDashboard />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/tasks/:gameId" 
            element={
              <ProtectedRoute allowedRoles={['participant', 'instructor', 'admin']}>
                <DashboardLayout>
                  <TaskList />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/solve-task/:taskId" 
            element={
              <ProtectedRoute allowedRoles={['participant', 'instructor', 'admin']}>
                <DashboardLayout>
                  <TaskSolve />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/reflection/:gameId" 
            element={
              <ProtectedRoute allowedRoles={['participant', 'instructor', 'admin']}>
                <DashboardLayout>
                  <Reflection />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          
          {/* Instructor routes */}
          <Route 
            path="/instructor/dashboard" 
            element={
              <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                <DashboardLayout>
                  <InstructorDashboard />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/create-game" 
            element={
              <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                <DashboardLayout>
                  <GameCreate />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/manage-game/:gameId" 
            element={
              <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                <DashboardLayout>
                  <GameManage />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/create-task/:gameId" 
            element={
              <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                <DashboardLayout>
                  <TaskCreate />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/evaluate-submission/:submissionId" 
            element={
              <ProtectedRoute allowedRoles={['instructor', 'admin']}>
                <DashboardLayout>
                  <SubmissionEvaluate />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          
          {/* Admin routes */}
          <Route 
            path="/admin/dashboard" 
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <DashboardLayout>
                  <AdminDashboard />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/users" 
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <DashboardLayout>
                  <UserManagement />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/admin/statistics/:gameId" 
            element={
              <ProtectedRoute allowedRoles={['admin']}>
                <DashboardLayout>
                  <GameStatistics />
                </DashboardLayout>
              </ProtectedRoute>
            } 
          />
          
          {/* Fallback route */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
        <ToastContainer 
          position="bottom-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
          className="z-50"
        />
      </Router>
    </AuthProvider>
  );
}

export default App;
